import React from "react";

function Avatar({ srcImage, size }) {
  return (
    <img
      src={srcImage}
      style={{ borderRadius: "30px", marginLeft: "10px" }}
      width={size}
      height={size}
      alt="imagem pessoa"
    />
  );
}

export default Avatar;
